import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <div class="container">
      <header>
        <img src="assets/Evans Mwaura Image.jpg" alt="Evans Mwaura" class="profile-pic">
        <h1>Evans Mwaura</h1>
        <h2>Web Developer | Computer Science Student</h2>
        <a href="assets/Evans_Mwaura_Mburu_resume.pdf" class="btn" download>
          üìÑ Download Resume
        </a>
      </header>

      <section class="quote">
        <p>"Code is like humor. When you have to explain it, it‚Äôs bad."</p>
      </section>

      <section class="about">
        <h2>About Me</h2>
        <p>
          Hi, I'm Evans! I‚Äôm passionate about building clean, modern, and user-friendly web applications. 
          Currently pursuing my Bachelor's in Computer Science, I‚Äôm always learning and experimenting with new technologies. 
          I‚Äôm also passionate about Cyber Security, Docker, and Kubernetes.
        </p>
      </section>

      <section class="projects">
        <h2>Projects</h2>
        <ul>
          <li>üí¨ Multi-Chama System ‚Äì A system for managing savings groups (chamas), contributions, and member tracking.</li>
          <li>üë®‚Äçüíº Employee Management System ‚Äì A platform for managing employees, attendance, and payroll.</li>
          <li>üåê Portfolio ‚Äì This Angular-based portfolio website showcasing my skills.</li>
        </ul>
      </section>

      <section class="contact">
        <h2>Contact Me</h2>
        <p>Email: evans@example.com</p>
        <p>
          <a href="https://github.com/your-username" target="_blank">üêô GitHub</a> |
          <a href="https://linkedin.com/in/your-username" target="_blank">üíº LinkedIn</a> |
          <a href="https://twitter.com/your-username" target="_blank">üê¶ Twitter</a>
        </p>
      </section>
    </div>
  `,
  styles: [`
    body {
      font-family: Arial, sans-serif;
      margin: 0;
      background: #f9f9f9;
      color: #333;
    }

    .container {
      max-width: 900px;
      margin: 0 auto;
      padding: 20px;
    }

    header {
      text-align: center;
      margin-bottom: 30px;
    }

    .profile-pic {
      width: 150px;
      height: 150px;
      border-radius: 50%;
      object-fit: cover;
      margin-bottom: 15px;
      border: 3px solid #007acc;
    }

    header h1 {
      font-size: 2.5rem;
      margin: 0;
    }

    header h2 {
      font-weight: 400;
      color: #666;
    }

    .btn {
      display: inline-block;
      margin-top: 15px;
      padding: 10px 20px;
      background: #007acc;
      color: white;
      text-decoration: none;
      border-radius: 5px;
    }

    .btn:hover {
      background: #005fa3;
    }

    section {
      margin-bottom: 40px;
    }

    h2 {
      border-bottom: 2px solid #eee;
      padding-bottom: 5px;
    }

    ul {
      list-style: none;
      padding: 0;
    }

    li {
      margin: 10px 0;
    }

    a {
      color: #007acc;
      text-decoration: none;
    }

    a:hover {
      text-decoration: underline;
    }
  `]
})
export class AppComponent {
  title = 'evans-mwaura-portfolio';
}
